    public void telaCadastro() {
        new TelaCadastroCliente().setVisible(true);
        TelaCadastroCliente CadastroCliente = new TelaCadastroCliente();
        BtnCadastroCliente.add(CadastroCliente);
        CadastroCliente.show();
    }

